const initialState = {
  ContactData: [],
};
const contactReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD-CONTACT":
      return {
        ContactData: state.ContactData.concat(action.payload),
      };
    case "DELETE-CONTACT":
      return {
        ContactData: state.ContactData.filter((cont) => cont.id !== action.id),
      };
    case "EDIT-CONTACT":
      let editedContacts = [...state.ContactData];

      editedContacts[action.updateData.id - 1] = action.updateData;
      return {
        ContactData: editedContacts,
      };

    default:
      return {
        ContactData: state.ContactData,
      };
  }
};
export default contactReducer;
