import { ADD_CONTACT, EDIT_CONTACT, REMOVE_CONTACT } from "../const";

export const Add_Contact = (payload) => {
  return {
    type: ADD_CONTACT,
    payload: payload,
  };
};
export const Remove_Contact = (payload) => {
  return {
    type: REMOVE_CONTACT,
    payload: payload,                      //need id
  };
};
export const Edit_Contact = (payload) => {
  return {
    type: EDIT_CONTACT,
    payload: payload,
  };
};
