import React, { useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import "./AddContact.css";
export const AddContact = () => {
  const { ContactData } = useSelector((data) => data.Contact);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const NameRef = useRef();
  const EmailRef = useRef();
  const PhoneRef = useRef();
  const addContact = () => {
    let newid = 0;
    if (ContactData.length === 0) {
      newid = 1;
    } else {
      let lastElement = ContactData[ContactData.length - 1];
      newid = lastElement.id + 1;
    }
    const payload = {
      id: newid,
      name: NameRef.current.value,
      email: EmailRef.current.value,
      phone: PhoneRef.current.value,
    };

    dispatch({ type: "ADD-Contact", payload: payload });
    navigate("/displayPage");
  };
  return (
    <div className="add-container">
      <div className="add-container-top">
        <h1>Add-Contact</h1>
        <button>Show-Contacts</button>
      </div>
      <div className="add-input-container">
        <form>
          <label>Name</label>
          <input type="text" ref={NameRef} />
          <label>Email</label>
          <input type="text" ref={EmailRef} />
          <label>Phone</label>
          <input type="text" ref={PhoneRef} />
          <button onClick={addContact}>Add-Use</button>
        </form>
        
      </div>
      <p>{NameRef.current.value}</p>
    </div>
  );
};
