const initialState = {
  ContactData: [],
};
const contactReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD-Contact":
      return {
        ContactData: state.ContactData.concat(action.payload),
      };
    case "DELETE-Contact":
      return {
        ContactData: state.ContactData.filter((cont) => cont.id !== action.id),
      };
    case "Edit-Contact":
      return {
        ContactData:state.ContactData[action.updateData.id] = action.updateData,
    }

    default:
      return {
        ContactData: state.ContactData,
      };
  }
};
export default contactReducer;
