const ContactData = [];
const contactReducer = (state = { ContactData:ContactData }, action) => {
  switch (action.type) {
    case "ADD-Contact":
      return {
        ContactData: state.ContactData.concat(action.payload),
      };
    case "DELETE-Contact":
      return {
        ContactData: state.ContactData.concat(action.payload),
      };

    default:
      break;
  }
};
export default contactReducer;
