import { act } from "react-dom/test-utils";
import { ADD_CONTACT, EDIT_CONTACT, REMOVE_CONTACT } from "../const";

const initialState = {
  contacts: [],
};
const contactReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_CONTACT: {
      return {
        contacts: state.contacts((cont) => [...cont, action.payload]),
      },
      console.log(state.contacts);
    }
    case REMOVE_CONTACT:
      return {
        contacts: state.contacts + action.payload,
      };
    case EDIT_CONTACT:
      return {
        contacts: state.contacts + action.payload,
      };

    default:
      return {
        contacts: state.contacts,
      };
  }
};
export default contactReducer;
