import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { Contact } from "../Contacts/Contact";
import { Fetch_contact, Fetch_contatc, Remove_Contact } from "../Redux/Action/contactAction";
import "./DisplayPage.css";
export const DisplayPage = () => {
  const dispatch = useDispatch();
  const contacts = useSelector((state) => state.contactReducer.contacts);
  const navigate = useNavigate();
  const onDeleteHandler = (id) => {
    dispatch(Remove_Contact(id));
  };
  const onEditHandler = (data) => {
    navigate("/editcontact", { state: data });
  };
  useEffect(()=>{
    const fetchcontacts = async () => {
      const response = await fetch(
        "https://contact-app-da957-default-rtdb.firebaseio.com/contact.json"
      );
      const responseData = await response.json();

      const loadedContacts = [];

      for (const key in responseData) {
        loadedContacts.push({
          key: key,
          id: responseData[key].id,
          name: responseData[key].name,
          email: responseData[key].email,
          phone: responseData[key].phone,
        });
      }
      dispatch(Fetch_contact(loadedContacts));
    };

    fetchcontacts();
  })
  return (
    <div className="display-container">
      <div className="showDetails">
        <header>
          <p id="Id">Id</p>
          <p id="Name">Name</p>
          <p id="Email">Email</p>
          <p id="Phone">Phone</p>
        </header>
        <div className="content-div">
          {contacts.map((item) => {
            return (
              <Contact
                key={item.id}
                id={item.id}
                name={item.name}
                email={item.email}
                phone={item.phone}
                onDelete={() => onDeleteHandler(item.id)}
                onEdit={() => onEditHandler(item)}
              />
            );
          })}
        </div>
      </div>
      <button
        onClick={() => {
          navigate("/");
        }}
      ></button>
    </div>
  );
};
