const ContactData = [null];
const contactReducer = (state = { ContactData }, action) => {
  switch (action.type) {
    case "ADD-Contact":
      return {
        ContactData: state.ContactData.concat(action.payload),
      };
    case "DELETE-Contact":
      return {
        ContactData: state.ContactData.filter((cont) => cont.id !== action.id),
      };

    default:
      break;
  }
};
export default contactReducer;
