import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { Route, Routes } from "react-router-dom";
import "./App.css";
import { AddContact } from "./Component/AddContact/AddContact";
import { DisplayPage } from "./Component/DisplayContact/DisplayPage";
import { Editcontact } from "./Component/EditContact/Editcontact";
import { Fetch_contact, Toggle_Spinner } from "./Component/Redux/Action/contactAction";
import { Header } from "./Component/UI/Header";

function App() {
  const dispatch=useDispatch()
  useEffect(() => {
    const fetchcontacts = async () => {
      dispatch(Toggle_Spinner(true));
      const response = await fetch(
        "https://contact-app-da957-default-rtdb.firebaseio.com/contact.json"
      );
      const responseData = await response.json();

      const loadedContacts = [];

      for (const key in responseData) {
        loadedContacts.push({
          key: key,
          id: responseData[key].id,
          name: responseData[key].name,
          email: responseData[key].email,
          phone: responseData[key].phone,
        });
      }
      dispatch(Fetch_contact(loadedContacts));
      if (responseData.ok) {
        dispatch(Toggle_Spinner(false));
      }
    };

    fetchcontacts();
  }, []);
  return (
    <div className="App">
      <Header />
      <Routes>
        <Route path="/Add-contact" element={<AddContact />} />
        <Route path="/displayPage" element={<DisplayPage />} />
        <Route path="/editcontact" element={<Editcontact />} />
        <Route path="/" element={<AddContact />} />
        <Route path="*" element={<AddContact />} />
      </Routes>
    </div>
  );
}

export default App;
